import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

const Navigation = ({onRouteChange, isSignedIn}) => {
		if (isSignedIn) {
			return(
				<nav style={{display: 'flex', justifyContent:'flex-end'}}>
				    <p onClick={() => onRouteChange('home')} className='f3 link dim black underline pa3 pointer'>About</p>
					<p onClick={() => onRouteChange('Terms and Conditions')} className='f3 link dim black underline pa3 pointer'>Terms and Conditions</p>
					<p onClick={() => onRouteChange('SignOut')} className='f3 link dim black underline pa3 pointer'>Sign out</p>
				</nav>
			);
		} else {
			return(
				<nav style={{display: 'flex', justifyContent:'flex-end'}}>
					<p onClick={() => onRouteChange('SignIn')} className='f3 link dim black underline pa3 pointer'>Sign In</p>
					<p onClick={() => onRouteChange('Register')} className='f3 link dim black underline pa3 pointer'>Register</p>
				</nav>
			);
		}
}

export default Navigation;
